import React, { FC } from "react"
import { Head } from "next/document"

interface MetaProperty {
  property: string
  content: string
}

interface MetaName {
  name: string
  content: string
}

type Meta = MetaName | MetaProperty

export interface SeoProps {
  title: string
  description: string
  meta?: Meta[]
}

export const Seo: FC<SeoProps> = ({ description, title, meta = [] }) => {
  const baseMeta: Meta[] = [
    {
      name: `description`,
      content: description,
    },
    {
      property: `og:title`,
      content: title,
    },
    {
      property: `og:description`,
      content: description,
    },
    {
      property: `og:type`,
      content: `website`,
    },
    {
      name: `twitter:card`,
      content: `summary`,
    },
    {
      name: `twitter:title`,
      content: title,
    },
    {
      name: `twitter:description`,
      content: description,
    },
  ]

  return (
    <>
      <Head>
        <title>{title}</title>
        <meta name="description" content={description} />

        {[...baseMeta, meta].map(m => (
          <meta key={m.name} name={m.name} content={m.content} />
        ))}
      </Head>
    </>
  )
}
