import React, { useState, FC, ReactNode } from "react"
import App, { AppContext } from "next/app"
import { ThemeProvider, createGlobalStyle } from "styled-components"
import MobileDetect from "mobile-detect"

import { PropsWithTheme, DeviceType } from "../types"

import { defaultTheme } from "../constants"

import Context, {
  initContext,
  AppContextInterface,
} from "../contexts/appContext"

const GlobalStyle = createGlobalStyle`
  html {
    font-size: 62.5%;
  }

  body {
    margin: 0;
    padding: 0;
    font-family: 'Roboto', sans-serif;
    color: ${({ theme }: PropsWithTheme) => theme.colors.common.text};
    font-size: 1.6em;
  }
`

interface LayoutProps {
  children: ReactNode
  userAgent: string
}

export const Layout: FC<LayoutProps> = ({ children, userAgent }) => {
  const [deviceType, setDeviceType] = useState<DeviceType>("sm")

  const mobileDetect = new MobileDetect(userAgent)

  const appContext: AppContextInterface = {
    ...initContext,
    deviceType,
    setDeviceType,
  }

  return (
    <ThemeProvider theme={defaultTheme}>
      <Context.Provider value={appContext}>
        <GlobalStyle />
        {children}
      </Context.Provider>
    </ThemeProvider>
  )
}

export default class AppComponent extends App {
  static getInitialProps({ ctx }: AppContext): any {
    return {
      props: {
        userAgent: ctx?.req?.headers["user-agent"],
      },
    }
  }

  render(): JSX.Element {
    const { Component, pageProps, userAgent } = this.props

    return (
      <Layout userAgent={userAgent}>
        <Component {...pageProps} />
      </Layout>
    )
  }
}
